import React from 'react';
// import classes from './Ads.module.css'
import logo from "../../assests/Logo.png";
import { useLocation } from 'react-router-dom';
import { useEffect, useState } from 'react';


export default function Ads(props) {
  const location = useLocation();
  const AdId = location.pathname.split('/')[2];

  const [post, setPost] = useState(null);

  useEffect(() => { // use this in database to fetch that particular Ad
    const fetchPost = async () => {
      const response = await fetch('http://localhost:4000/ad-api/getAds/id/' + AdId);   // don't include colon for params while actually fetching.
      const thatPost = await response.json();
      setPost(thatPost);
    }
    fetchPost();
  }, []);

  return (
    <div> 
      <div>
        {post.pro_image}
      </div>
      <h1>Description : {post.description}</h1>
      <p>The ad Id is {AdId}</p>
    </div>
  )
}
