import Backdrop from '../UserInterface/Backdrop';
import Modal from './Modal';
import { useState } from 'react';

const LoginLink = () => {
    const [modalisOpen, setmodalisOpen] = useState(false);

    function showModal(){
        setmodalisOpen(true);
    }

    function removeModal(){
        setmodalisOpen(false);
    }

    return (
        <div>
            <strong style={backg} onClick={showModal}>
                    <u>
                        Login
                    </u>
            </strong>
            {modalisOpen ? <Modal clickButtons={removeModal} /> : null}
            {modalisOpen ? <Backdrop clickBackdrop={removeModal} /> : null}
        </div>
    );
}
 
export default LoginLink;
