import Backdrop from '../UserInterface/Backdrop';
import Modal from './Modal';
import { useState } from 'react';

const LoginLink = (props) => {
    const [loginmodalisOpen, setloginmodalisOpen] = useState(false);

    function showloginModal(){
        setloginmodalisOpen(true);
    }

    function removeloginModal(){
        setloginmodalisOpen(false);
    }

    return (
        <div>
            <strong style={{backgroundColor: 'blue', cursor: 'pointer'}} onClick={showloginModal}>
                    <u>
                        Login
                    </u>
            </strong>
            {loginmodalisOpen ? <Modal logintohome={removeloginModal} loginclick={props.loginclick} /> : null}
            {modalisOpen ? <Backdrop clickBackdrop={removeloginModal} /> : null}
        </div>
    );
}
 
export default LoginLink;
