import classes from './Modal.module.css';
import Card from '../UserInterface/Card';
import { Link } from 'react-router-dom';
import { useFormik } from "formik";
import { useState } from 'react';
import Signupmodal from '../Signupthingys/Signupmodal';
import Backdrop from '../UserInterface/Backdrop';

function Modal(props){
    // const click = props.logintohome;
    const [signupmodalisOpen, setsignupmodalisOpen] = useState(false);

    function showSignupmodal(){
        setsignupmodalisOpen(true);
    }

    function removeSignupmodal(){
        setsignupmodalisOpen(false);
    }
    const formik = useFormik({
        initialValues: {
            Email: '',
            Password: ''
        },
        onSubmit: values => {
            console.log(values);
        }
    });

    function firefunction() {
        props.removeloginModal();
        props.loginclick();
    }
    function firefunction2(){
        props.removeloginModal();
        props.loginclick();
        showSignupmodal();
    }
    // console.log(formik.values, formik.Email.values);

    return (
        <Card>
        <div className={classes.modal}>
            <p>
                Login Here
            </p>
            <div>
                <form onSubmit={formik.handleSubmit}>
                    <div>
                        <label className={classes.criteria1} htmlFor='email'>Enter your IITG mail-id</label>
                        <br />
                        <input 
                        className={classes.input1} 
                        id='email' 
                        name='Email' 
                        type="email" 
                        placeholder="include@iitg.ac.in" 
                        required 
                        onChange={formik.handleChange}
                        value={formik.values.Email}/>
                    </div>
                    <div>
                        <label className={classes.criteria2} htmlFor='pass'>Enter Password</label>
                        <br />
                        <input 
                        className={classes.input2} 
                        id='pass'
                        name='Password'
                        type="password"
                        placeholder='********'
                        required 
                        onChange={formik.handleChange}
                        value={formik.values.Password}/>
                    </div>
                </form>
            </div>
            <div>
                <button type='submit' className={classes.btn} onClick={firefunction2}>Login</button>
            </div>
            <div>
                <Link to='/'>
                    Forgot Password?
                </Link>
            </div>
            <div>
                <Link to='/'>
                    <button type='button' onClick={firefunction}>Create New Account</button>
                </Link>
            </div>
            {signupmodalisOpen ? <Signupmodal removeSignupmodal={removeSignupmodal} /> : null}
            {signupmodalisOpen ? <Backdrop clickBackdrop={firefunction} /> : null}
        </div>
        </Card>
    );
}
 
export default Modal;
